function full_data = predict(w, input_patterns)  # Initialization  v = {};    levels = length(w);  for i = 1 : size(input_patterns)(1)    current_pattern = input_patterns(i,1:2);      v{1} = [-1, current_pattern];    for k = 1 : levels - 1       v{k + 1} = [-1, hyp_tan((w{k} * v{k}')')];    endfor    v{levels + 1} =  hyp_tan((w{end} * v{levels}')');    output(i) = v{end};  endfor  full_data = [input_patterns(:,1:2), output'];endfunction